const titles = output.title_filter;
const keywords = output.keywords_filter;

// Reconstruir URL con la estructura de filtros de Sales Navigator
const encodedTitles = encodeURIComponent(titles);
const encodedKeywords = encodeURIComponent(keywords);
const encodedGeo = encodeURIComponent(geoName);

// Construcción del link con soporte para ID o Texto
const finalUrl = `https://www.linkedin.com/sales/search/people?query=(filters:List((type:REGION,values:List((id:${geoId},text:${encodedGeo},selectionType:INCLUDED))),(type:CURRENT_TITLE,values:List((text:${encodedTitles},selectionType:INCLUDED)))),keywords:${encodedKeywords})`;

const finalBoolean = `TITLES: (${titles}) | KEYWORDS: (${keywords}) | GEO: ${geoName}`;

const fecha = new Date().toLocaleDateString('es-CO', {
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

let docContent = `
───────────────────────────────────────────────────────────────
                    ETAPA 5 - SALES NAVIGATOR ENCODER
                    Generado: ${fecha}
───────────────────────────────────────────────────────────────
**Estrategia de Búsqueda:**
${finalBoolean}

**Link de Búsqueda (Sales Navigator):**
${finalUrl}
───────────────────────────────────────────────────────────────
`;

return [{
  json: {
    contentToAppend: docContent,
    documentId: $('E5: Leer Personal Strategy').first().json.documentId,
    booleanString: finalBoolean,
    searchLink: finalUrl
  }
}];

const url = $input.first().json['URL del Documento Personal Strategy'];
let docId = '';
const match = url.match(/\/d\/([a-zA-Z0-9_-]+)/);
if (match) {
  docId = match[1];
} else {
  docId = url.trim();
}
