const text = $input.first().json.content;
const docId = $('E6: Extraer ID del Documento').first().json.documentId;

// Normaliza saltos de línea por si vienen como \r\n (Windows)
const normalizedText = text.replace(/\r\n/g, '\n');

// 1. Extraer Folder ID
// Soporta: Folder_id, folder_id, Folder ID, etc.
const folderMatch = normalizedText.match(/^\s*Folder[_\s-]*id\s*:\s*([A-Za-z0-9_-]+)\s*$/mi);
const folderId = folderMatch ? folderMatch[1].trim() : '';

// 2. Extraer Boolean String (para PhantomBuster)
const booleanMatch = normalizedText.match(/\*\*Boolean String:\*\*\s*\n([^\n]+)/);
const booleanString = booleanMatch ? booleanMatch[1].trim() : '';

// 3. Extraer Link de Búsqueda
// Acepta "Link de Búsqueda:" o "Link de Búsqueda (Sales Navigator):"
const linkMatch = normalizedText.match(
  /\*\*Link de Búsqueda(?:\s*\(.*?\))?:\*\*\s*\n\s*(https?:\/\/[^\s\n]+)/
);
const searchLink = linkMatch ? linkMatch[1].trim() : '';

// 4. Extraer ICP Section (para scoring)
const icpMatch = normalizedText.match(/### B\. PERFIL ICP[\s\S]*?(?=### C\. DECISIÓN)/);
const icpSection = icpMatch ? icpMatch[0].trim() : '';

// 5. Extraer Buyer Persona Section (para scoring)
const personaMatch = normalizedText.match(/### E4: BUYER PERSONA[\s\S]*?(?=### VALIDACIÓN|───)/);
const buyerPersonaSection = personaMatch ? personaMatch[0].trim() : '';

// 6. Extraer Configuración Inicial (para scoring)
const configMatch = normalizedText.match(
  /CONFIGURACIÓN INICIAL[\s\S]*?PROPUESTA DE VALOR:[^\n]+/
);
const configSection = configMatch ? configMatch[0].trim() : '';

return [{
  json: {
    documentId: docId,
    folderId,
    booleanString,
    searchLink,
    icpSection,
    buyerPersonaSection,
    configSection,
    fullText: text
  }
}];

