// Obtener leads de PhantomBuster y secciones de scoring
const phantomOutput = $input.all()
const sections = $('E6: Extraer Secciones').first().json;

// Los leads vienen en el output de PhantomBuster
let rawLeads = phantomOutput.map(item => item.json);

// Campos a EXCLUIR
const excludeFields = [
  'companyId', 'profileImageUrl', 'sharedConnectionsCount',
  'vmid', 'query', 'searchAccountProfileId', 'searchAccountProfileName'
];

// Filtrar campos y limitar a 30 leads
const leads = rawLeads.slice(0, 30).map(lead => {
  const filtered = {};
  for (const [key, value] of Object.entries(lead)) {
    if (!excludeFields.includes(key)) {
      filtered[key] = value;
    }
  }
  return filtered;
});

console.log(leads)

// Extraer empresas unicas con ubicacion e industria
const empresasMap = new Map();
for (const lead of leads) {
  const empresa = lead.companyName;
  if (empresa && !empresasMap.has(empresa)) {
    empresasMap.set(empresa, {
      empresa: empresa,
      empresa_url: lead.companyUrl || '',
      industria: lead.industry || '',
      ubicacion: lead.companyLocation || lead.location || '',
      numLeads: 1,
      leads: [lead.name]
    });
  } else if (empresa) {
    const existing = empresasMap.get(empresa);
    existing.numLeads += 1;
    existing.leads.push(lead.name);
  }
}
const empresas = Array.from(empresasMap.values());

// Contexto para scoring (del documento)
const scoringContext = {
  icp: sections.icpSection,
  buyerPersona: sections.buyerPersonaSection,
  config: sections.configSection
};

// Fecha para el Sheet
const fecha = new Date().toLocaleDateString('es-CO', {
  year: 'numeric', month: 'long', day: 'numeric'
});

return [{
  json: {
    leads,
    empresas,
    scoringContext,
    documentId: sections.documentId,
    folderId: sections.folderId,
    sheetTitle: `Wakee Leads - ${fecha}`,
    totalLeadsFound: rawLeads.length
  }
}];

// Obtener scores del LLM y datos originales
const llmOutput = $input.first().json.output || $input.first().json;
const filterData = $('E6: Filtrar Data PhantomBuster').first().json;
const spreadsheetId = $('E6: Crear Google Sheet').first().json.spreadsheetId;

// Parsear scores (el LLM devuelve array con formato compacto)
let scores = llmOutput

// Mapear scores a leads originales
const leadsWithScores = filterData.leads.map((lead, idx) => {
  const score = scores.find(s => s.i === idx + 1) || {};
  return {
    rowNumber: idx + 2, // Fila en Sheet (1 es header)
    nombre: lead.name,
    scoreIndustria: score.sInd,
    scoreTitulos: score.scoreTitulos,
    scorePerfilEmp: score.scorePerfilEmpresa,
    scoreSeniority: score.scoreSeniority,
    scoreDolor: score.scoreDolor,
    scoreGeo: score.scoreGeo,
    scoreTotal:   Math.round(
    (
      (score.sInd * 0.35) +
      (score.scoreTitulos * 0.3) +
      (score.scorePerfilEmpresa * 0.05) +
      (score.scoreSeniority * 0.1) +
      (score.scoreDolor * 0.1) +
      (score.scoreGeo * 0.1)
    ) * 100
  ) / 100
    ,justificacion: score.justificacion
  };
});

// Ordenar por score total
leadsWithScores.sort((a, b) => b.scoreTotal - a.scoreTotal);

return [{
  json: {
    spreadsheetId,
    leadsWithScores,
    empresas: filterData.empresas,
    documentId: filterData.documentId
  }
}];

// Preparar datos para actualizar scores en el Sheet
const data = $input.first().json;

// Crear items individuales para actualizar cada fila de scores
const scoreUpdates = data.leadsWithScores.map(lead => ({
  json: {
    spreadsheetId: data.spreadsheetId,
    Nombre: lead.nombre,
    'Score Industria': lead.scoreIndustria,
    'Score Titulos': lead.scoreTitulos,
    'Score Perfil Emp': lead.scorePerfilEmp,
    'Score Seniority': lead.scoreSeniority,
    'Score Dolor': lead.scoreDolor,
    'Score Geo': lead.scoreGeo,
    'Score Total': lead.scoreTotal,
    Justificacion: lead.justificacion
  }
}));

return scoreUpdates;

