const item = $input.first().json;
const content = item.content || "";

// Extrae texto entre dos marcadores (start incluido; end excluido)
function extractBetween(text, startRegex, endRegex) {
  const start = text.search(startRegex);
  if (start === -1) return null;

  const sliced = text.slice(start);
  const endMatch = sliced.match(endRegex);
  if (!endMatch) return sliced.trim();

  return sliced.slice(0, endMatch.index).trim();
}

// Elimina una subsección tipo "#### X" hasta el siguiente encabezado (#### o ###) o fin.
function removeSubsection(block, subsectionTitleRegex) {
  if (!block) return block;

  const pattern = new RegExp(
    String.raw`^####\s*${subsectionTitleRegex.source}\s*[\s\S]*?(?=^####\s|\n###\s|^###\s|$)`,
    "gmi"
  );

  return block.replace(pattern, "").replace(/\n{3,}/g, "\n\n").trim();
}

// 1) Sección B: desde B hasta C
const icpSection = extractBetween(
  content,
  /###\s*B\.\s*PERFIL ICP E HIPÓTESIS DE DOLOR/i,
  /###\s*C\.\s*DECISIÓN DEL BROKER/i
);

// 2) Sección E4: desde E4 hasta VALIDACIÓN (o hasta G como fallback)
let e4Section = extractBetween(
  content,
  /###\s*E4:\s*BUYER PERSONA/i,
  /###\s*VALIDACIÓN DEL BROKER/i
);

if (!e4Section) {
  e4Section = extractBetween(
    content,
    /###\s*E4:\s*BUYER PERSONA/i,
    /###\s*G\.\s*RANKING FINAL DE LEADS/i
  );
}

// 3) Quitar subsecciones no deseadas dentro de E4
// Ojo: estamos removiendo bloques completos que empiezan con esos encabezados.
e4Section = removeSubsection(e4Section, /FILTROS OPERACIONALES/i);
e4Section = removeSubsection(e4Section, /NOTAS PARA EL BROKER/i);

return [
  {
    json: {
      documentId: item.documentId || null,
      ICP: icpSection,
      buyerPersona: e4Section
    }
  }
];

