// Datos del nodo anterior (antes del Wait)
// const preWaitData = $('E3: Formatear ICPs').first().json;

// Datos del formulario HITL (selección del broker)
const hitlData = $("E3: HITL Seleccionar ICP1").first().json

const fecha = new Date().toLocaleDateString('es-CO', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Extraer solo el ICP seleccionado del contenido completo
const selectedICP = hitlData['ICP Seleccionado']; // "ICP 1", "ICP 2", o "ICP 3"
const icpNumber = selectedICP.match(/^ICP\s+(\d+)/)[1];

const icpGenerationOutput = $('E3: Generar ICP basado perfil').first().json.output
const selectedIcpObject = icpGenerationOutput.icps.find(
  icpCandidate => icpCandidate.rank == icpNumber
);

const folderId = $("E3: Crear Carpeta Personal1").first().json.id

const selectedIcpWithContext = {
  serviceContext: icpGenerationOutput.service_context,
  selectedIcp: selectedIcpObject
};


//--------------------  Sección de respuesta del formulario 
const onBoardingResponses = $("E3: Formulario Onboarding").first().json
const brokerName = onBoardingResponses['Nombre del Broker']

const brokerResponse = onBoardingResponses; 
// reemplaza brokerFormResponse por tu variable real

const brokerSummaryText = `
### 
La estrategia fue iniciada por **${brokerResponse["Nombre del Broker"]}** bajo el foco operativo **${brokerResponse["Foco Operativo"]}**, específicamente en la subcategoría **${brokerResponse["Subcategoría de Foco Operativo "]?.trim()}**.

El análisis estratégico se fundamenta en el Master Intelligence Report disponible en:
${brokerResponse["URL del Master Intelligence Report"]}

Fecha de envío del formulario: ${new Date(brokerResponse.submittedAt).toLocaleString()}

Esta configuración define el marco estratégico bajo el cual se generaron los ICPs y las hipótesis de oportunidad inmobiliaria.
`.trim();

//-------------------  Sección de la definición del ICP 
const { serviceContext, selectedIcp } = selectedIcpWithContext; 

const icpSectionText = `
### B. PERFIL ICP E HIPÓTESIS DE DOLOR

**ICP Seleccionado (Rank ${selectedIcp.rank} | Score Estratégico ${selectedIcp.score_strategic}/10):**
${selectedIcp.name}

**Contexto del Servicio:**
- Category: ${serviceContext.category}
- Subcategory: ${serviceContext.subcategory}
- Vertical: ${serviceContext.vertical}

**Industria Específica Objetivo:**
${selectedIcp.industry_specific.map(i => `- ${i}`).join('\n')}

**Tipo de Entidad Objetivo:**
${selectedIcp.target_entity_type}

**Señal de Mercado Activadora:**
${selectedIcp.market_signal.description}
Fuente: ${selectedIcp.market_signal.source_name} – ${selectedIcp.market_signal.source_url}

**Caso de Uso Activado:**
${selectedIcp.use_case_activated}

**Hipótesis de Dolor Estratégico:**
${selectedIcp.strategic_pain_addressed}

**Intensidad de Movimiento (Capex):**
${selectedIcp.capex_intensity}

**Ventana de Oportunidad:**
${selectedIcp.opportunity_window}

**Validación Estratégica:**
- Respeta exclusiones: ${selectedIcp.validation.respects_exclusions ? "Sí" : "No"}
- Dentro de geografía objetivo: ${selectedIcp.validation.within_geography ? "Sí" : "No"}
- Activa caso de uso definido: ${selectedIcp.validation.activates_use_case ? "Sí" : "No"}
- Alineado con entidad objetivo: ${selectedIcp.validation.entity_alignment ? "Sí" : "No"}

Este ICP representa una oportunidad estratégica priorizada dentro del vertical ${serviceContext.vertical}, con base en una señal verificable del mercado y una hipótesis clara de transacción inmobiliaria bajo el caso de uso "${selectedIcp.use_case_activated}".
`.trim();



// Construir el contenido del documento Personal Strategy (SOLO con el ICP seleccionado)
let docContent = `
Folder_id: ${folderId}
═══════════════════════════════════════════════════════════════
        ESTRATEGIA DE PROSPECCIÓN PERSONAL
        ${brokerName}
        Generado: ${fecha}
═══════════════════════════════════════════════════════════════
${brokerSummaryText}

### A. CONTEXTO DE MERCADO APLICADO
(Referencia al Master Intelligence Report semanal)

${icpSectionText}

### C. DECISIÓN DEL BROKER

ICP Seleccionado: ${selectedICP}
`;

return [{
  json: {
    documentTitle: `Wakee Strategy - ${brokerName} - ${fecha}`,
    documentContent: docContent,
    brokerName: brokerName,
    selectedICP: selectedICP,
    selectedICPContent: icpSectionText
  }
}];

// n8n Code node
// Input esperado: $input.all() -> [{ json: { output: { service_context, icps } } }]
// Output: [{ json: { hitl_text, icp_options, service_context } }]

const items = $input.all();

// 1) Obtener el objeto "output" (tu ejemplo lo trae como item.json.output)
const first = items?.[0]?.json ?? items?.[0] ?? {};
const output = first.output ?? first.json?.output ?? first;

// Validaciones mínimas
if (!output || !output.icps || !Array.isArray(output.icps)) {
  return [{
    json: {
      error: "Input inválido: no se encontró output.icps como array.",
      receivedKeys: Object.keys(first || {})
    }
  }];
}

const service = output.service_context || {};
const icps = output.icps;

// 2) Helpers de formato
const capexMap = {
  "Alta": "Alta (inversión fuerte/probable)",
  "High": "Alta (inversión fuerte/probable)",
  "Media": "Media (inversión probable)",
  "Medium": "Media (inversión probable)",
  "Baja": "Baja (señal débil o temprana)",
  "Low": "Baja (señal débil o temprana)"
};

function safeList(arr) {
  if (!Array.isArray(arr) || arr.length === 0) return "—";
  return arr.map(s => `• ${String(s).trim()}`).join("\n");
}

function safeStr(v, fallback = "—") {
  if (v === null || v === undefined) return fallback;
  const s = String(v).trim();
  return s.length ? s : fallback;
}

function safeBool(v) {
  return v === true ? "Sí" : (v === false ? "No" : "—");
}

function scoreLine(scoreObj) {
  if (!scoreObj || typeof scoreObj !== "object") return "—";
  const parts = [
    ["Señal", scoreObj.signal_strength],
    ["Servicio", scoreObj.service_alignment],
    ["Caso de uso", scoreObj.use_case_activation],
    ["Capex", scoreObj.capex_intensity],
    ["Urgencia", scoreObj.urgency]
  ].map(([k, val]) => `${k}: ${Number.isFinite(val) ? val : safeStr(val)}/10`);
  return parts.join(" · ");
}

// 3) Header del texto HITL
const header = [
  "📌 **Selección de ICP (Human-in-the-Loop)**",
  "",
  "A continuación tienes 3 opciones de ICP generadas. Lee los resúmenes y elige **1 (uno)** para continuar.",
  "",
  "🧭 **Contexto del servicio**",
  `- Category: **${safeStr(service.category)}**`,
  `- Subcategory: **${safeStr(service.subcategory)}**`,
  `- Vertical: **${safeStr(service.vertical)}**`,
  "",
  "✅ **Instrucción:** responde con el **Rank** (1, 2 o 3) del ICP seleccionado.",
  ""
].join("\n");

// 4) Bloques por ICP
const blocks = icps
  .slice()
  .sort((a, b) => (a.rank ?? 999) - (b.rank ?? 999))
  .map(icp => {
    const ms = icp.market_signal || {};
    const val = icp.validation || {};
    const capexPretty = capexMap[safeStr(icp.capex_intensity)] || safeStr(icp.capex_intensity);

    return [
      `---`,
      `## ICP ${safeStr(icp.rank)} — **${safeStr(icp.name)}**`,
      ``,
      `**Score estratégico:** **${safeStr(icp.score_strategic)}/10**`,
      ``,
      `**Industria específica (para prospectar):**`,
      `${safeList(icp.industry_specific)}`,
      ``,
      `**Tipo de empresa objetivo:** ${safeStr(icp.target_entity_type)}`,
      ``,
      `**Señal de mercado (evidencia):** ${safeStr(ms.description)}`,
      `↳ Fuente: ${safeStr(ms.source_name)} — ${safeStr(ms.source_url)}`,
      ``,
      `**Use Case activado:** ${safeStr(icp.use_case_activated)}`,
      `**Dolor estratégico:** ${safeStr(icp.strategic_pain_addressed)}`,
      ``,
      `**Intensidad de inversión (Capex):** ${capexPretty}`,
      `**Ventana de oportunidad:** ${safeStr(icp.opportunity_window)}`,
      ``,
      `**Detalle del score:** ${scoreLine(icp.score_breakdown)}`,
      ``,
      `**Validación contra reglas del servicio:**`,
      `- Respeta exclusiones: **${safeBool(val.respects_exclusions)}**`,
      `- Dentro de geografía: **${safeBool(val.within_geography)}**`,
      `- Activa caso de uso: **${safeBool(val.activates_use_case)}**`,
      `- Ataca dolor declarado: **${safeBool(val.addresses_declared_pain)}**`,
      `- Alineación entidad: **${safeBool(val.entity_alignment)}**`,
      ``,
      `**Fuentes usadas:**`,
      (Array.isArray(icp.sources) && icp.sources.length)
        ? icp.sources.map((s, i) => `- [${i + 1}] ${safeStr(s.name)} — ${safeStr(s.url)}`).join("\n")
        : "—"
    ].join("\n");
  })
  .join("\n");

// 5) Cierre + opciones para un form
const footer = [
  "",
  "---",
  "### 👉 Tu selección",
  "Indica el **Rank** del ICP con el que quieres continuar: **1**, **2** o **3**.",
  ""
].join("\n");

const hitl_text = `${header}\n${blocks}\n${footer}`;

// Opciones estructuradas por si quieres crear un n8n Form (dropdown/radio)
const icp_options = icps
  .slice()
  .sort((a, b) => (a.rank ?? 999) - (b.rank ?? 999))
  .map(icp => ({
    value: String(icp.rank),
    label: `ICP ${icp.rank}: ${safeStr(icp.name)} (Score ${safeStr(icp.score_strategic)}/10)`,
    meta: {
      use_case_activated: safeStr(icp.use_case_activated),
      target_entity_type: safeStr(icp.target_entity_type),
      industry_specific: Array.isArray(icp.industry_specific) ? icp.industry_specific : []
    }
  }));

return [{
  json: {
    service_context: service,
    icp_options,
    hitl_text
  }
}];

